sys_script_include.script:
var RESTAPI = Class.create();
RESTAPI.prototype = {
    initialize: function(sInterfaceConfig) {
		this._cfg = new x_snc_json_cfg_mgr.getCfg(sInterfaceConfig);
		this.oConfig = this._cfg.getConfig();
		this._getValue = this._cfg._getValue;
	}, // function initialize

	process: function(oRequest, oResponse) {

		if (this._getValue('sAction') == 'read') {
			new RESTAPI_GET(this.oConfig).process(oRequest, oResponse);
		} else {
			new RESTAPI_POST_PUT(this.oConfig).process(oRequest, oResponse);
		} // if read
	}, // function process

	type: 'RESTAPI',
	version: '1.0.0',
	author: 'Tom Hauri',
	email: 'tom@hauri.biz'

};
