sys_script_include.script:
var TestConfig = Class.create();
TestConfig.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	renderHTML: function (sId) {
		var grConfiguration = new GlideRecord('x_snc_interface_cm_configuration');
		if (grConfiguration.get(sId)) {
			var _cfg = new Configuration(grConfiguration);
			return _cfg.renderTestConfig();
		} else {
			return '<div><p>SI not found: ' + sId + '</p></div>';
		} // if config
	}, // function renderHTML

    type: 'TestConfig',
	version: '1.0.0',
	author: 'Tom Hauri',
	email: 'tom@hauri.biz'
});
