sys_script_include.script:
var RESTAPI_POST_PUT = Class.create();
RESTAPI_POST_PUT.prototype = {
	initialize: function (sInterfaceConfig) {
		this._cfg = new x_snc_json_cfg_mgr.getCfg(sInterfaceConfig);
		this.oConfig = this._cfg.getConfig();
		this._getValue = this._cfg._getValue;
		this._gt = new global.GlobalTools();
		this._gtArray = new global.GlobalToolsArray();
		this._unique = this._gtArray.unique;
		this._isValidArray = this._gtArray.isValidArray;
		this._gtObject = new global.GlobalToolsObject();
		this._getValue = this._gtObject.getValue;
		this._setValue = this._gtObject.setValue;
		this._gtRead = new global.GlobalToolsRecordRead();
		this._gtRecordWriteValidate = new global.GlobalToolsRecordWriteValidate();
	}, // function initialize

	process: function (oRequest, oResponse) {
		this.oRequest = oRequest;
		this.oResponse = oResponse;
		try {
			var oError = {};
			var oRecordValues = this.oRequest.body.data;
			var sRecordId = this._gtRecordWriteValidate(this.oConfig, oRecordValues, oError);
			if ((!sRecordId) || (sRecordId == '-1') || (!this._getValue('bFatalError', false, oError)) || (!this._getValue('bNoActionError', false, oError))) {
				this.oResponse.setContentType('application/json');
				this.oResponse.setStatus(this.iResponseStatusCode); // NN 
				this.oResponse.setBody(this.oResponseBody);
			} else {
				this.oResponse.setContentType('application/json');
				this.oResponse.setStatus(this.iResponseStatusCode); // NN 
				this.oResponse.setBody(this.oResponseBody);
			}



		} catch (oEx) {
			this._setError('400_002', [], [String(oEx)]);
			return true;
		} // if request body

	}, // function process

	type: 'RESTAPI_POST_PUT',
	version: '1.0.0',
	author: 'Tom Hauri',
	email: 'tom@hauri.biz'
};
